document.write('<li><a href="' + relatedUrls[r] + '">' +   
relatedTitles[r] + '</a></li>');  
if (r < relatedTitles.length - 1) {  
r++;  
} else {  
r = 0;}  
i++;}  
document.write('</ul>');}  
